import React, { PureComponent } from 'react';
import { Redirect, Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { LoginWrapper, LoginBox, Input, Button } from './style';
import { actionCreators } from './store';


class Register extends PureComponent {

	addAccount = _ => {
		const {product} = this.state;
		fetch(`https://localhost:3000/register/add?username=${product.account}&password=&{product.password}`)
	}

	renderProduct = ({product_id, name}) => <div key={product_id}>{name}</div>

	render() {
		const { product, products } = this.state;
		return (
		<div className = "App">
		{products.map(this.renderProduct)}
		<div>
		<input value={product.account} onChange={e => this.setState({product: {...product,name: e.target.value}})}/>
		<input value={product.password} onChange={e => this.setState({product: {...product,price: e.target.value}})}/>
		<button onClick={this.addProduct}>Create Account</button>
		</div>
		</div>
		);
	} 
}

const mapState = (state) => ({
	loginStatus: state.getIn(['login', 'login'])
})

const mapDispatch = (dispatch) => ({
	register(accountElem, passwordElem){
		dispatch(actionCreators.register(accountElem.value, passwordElem.value))
	}
})

export default connect(mapState, mapDispatch)(Register);
